args:
/* Custom styles for uniform product cards */
.product-item {
    height: 100%;
    display: flex;
    flex-direction: column;
}

.product-img {
    height: 300px; /* Fixed height for images */
    width: 100%;
    overflow: hidden;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: #f8f9fa;
}

.product-img img {
    height: 100%;
    width: 100%;
    object-fit: cover; /* Ensures image covers the area without distortion */
}

.cat-item {
    height: 100px; /* Fixed height for category items */
    overflow: hidden;
}
